package week1.day2;

public class NumberPasitiveorNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number= 35; 
		if(number>0)
		{
			System.out.println("The Give Number is positive "+number);
		}
		else if(number<0)
		{
			System.out.println("The Give Number is Negative "+number);
		}
		else
		{
			System.out.println(" The Given Number is neither positive nor negative");
		}
	}
	

}
