package week1.day2;

public class SelectionIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=56538;
		
		if(number%2==0)
		{
			System.out.println("It is Even Number  "+number);
		}
		else
		{
			System.out.println("It is Not Even Number"+number);
		}
	}

}
